SELECT COUNT(DISTINCT column_name) as columns
FROM schema_profiles
WHERE application = ${application:singlequote}
  AND environment = ${env1:singlequote}
  AND table_name = ${table_name:singlequote}
  AND scan_time = (
    SELECT MAX(scan_time) FROM schema_profiles
    WHERE application = ${application:singlequote}
      AND environment = ${env1:singlequote}
      AND table_name = ${table_name:singlequote}
  )

SELECT COUNT(DISTINCT column_name) as columns
FROM schema_profiles
WHERE application = ${application:singlequote}
  AND environment = ${env2:singlequote}
  AND table_name = ${table_name:singlequote}
  AND scan_time = (
    SELECT MAX(scan_time) FROM schema_profiles
    WHERE application = ${application:singlequote}
      AND environment = ${env2:singlequote}
      AND table_name = ${table_name:singlequote}
  )

WITH env1_cols AS (
  SELECT column_name, data_type, is_nullable, column_default
  FROM schema_profiles
  WHERE application = ${application:singlequote}
    AND environment = ${env1:singlequote}
    AND table_name = ${table_name:singlequote}
    AND scan_time = (
      SELECT MAX(scan_time) FROM schema_profiles
      WHERE application = ${application:singlequote}
        AND environment = ${env1:singlequote}
        AND table_name = ${table_name:singlequote}
    )
),
env2_cols AS (
  SELECT column_name, data_type, is_nullable, column_default
  FROM schema_profiles
  WHERE application = ${application:singlequote}
    AND environment = ${env2:singlequote}
    AND table_name = ${table_name:singlequote}
    AND scan_time = (
      SELECT MAX(scan_time) FROM schema_profiles
      WHERE application = ${application:singlequote}
        AND environment = ${env2:singlequote}
        AND table_name = ${table_name:singlequote}
    )
)
SELECT COUNT(*) as differences
FROM (
  -- Columns only in env1
  SELECT e1.column_name FROM env1_cols e1
  LEFT JOIN env2_cols e2 ON e1.column_name = e2.column_name
  WHERE e2.column_name IS NULL
  UNION ALL
  -- Columns only in env2
  SELECT e2.column_name FROM env2_cols e2
  LEFT JOIN env1_cols e1 ON e2.column_name = e1.column_name
  WHERE e1.column_name IS NULL
  UNION ALL
  -- Type mismatches
  SELECT e1.column_name FROM env1_cols e1
  JOIN env2_cols e2 ON e1.column_name = e2.column_name
  WHERE e1.data_type != e2.data_type
     OR e1.is_nullable != e2.is_nullable
) diffs

