SELECT 
  COALESCE(e1.column_name, e2.column_name) as column_name,
  CASE
    WHEN e1.column_name IS NULL THEN '‚ùå Missing in ${env1}'
    WHEN e2.column_name IS NULL THEN '‚ùå Missing in ${env2}'
    WHEN e1.data_type != e2.data_type OR e1.is_nullable != e2.is_nullable THEN '‚ö†Ô∏è Type Mismatch'
    ELSE '‚úÖ Match'
  END as status,
  e1.data_type as "${env1} Type",
  e2.data_type as "${env2} Type",
  CASE WHEN e1.is_nullable THEN 'YES' ELSE 'NO' END as "${env1} Nullable",
  CASE WHEN e2.is_nullable THEN 'YES' ELSE 'NO' END as "${env2} Nullable",
  CASE WHEN e1.is_primary_key THEN 'üîë' ELSE '' END as "${env1} PK",
  CASE WHEN e2.is_primary_key THEN 'üîë' ELSE '' END as "${env2} PK",
  CASE WHEN e1.is_in_index THEN 'üìá' ELSE '' END as "${env1} Indexed",
  CASE WHEN e2.is_in_index THEN 'üìá' ELSE '' END as "${env2} Indexed"
FROM env1_cols e1
FULL OUTER JOIN env2_cols e2 ON e1.column_name = e2.column_name
ORDER BY COALESCE(e1.column_position, e2.column_position)

